            DT::dataTableOutput('table_as')
            #tags$style(HTML('table.dataTable tr.selected td, table.dataTable tr.selected {box-shadow: inset 0 0 0 9999px #126a8f !important;}'))
        ) #mainPanel
    )#sidebarLayout
